import styled from "styled-components";

export const BasicButton = styled.button`
  background: #05386B;
  border-style: none;
  font-family: Raleway;
  font-size: .76em;
  font-weight: 700;
  color: #EDF5E1;
  text-transform: uppercase;
  padding: 0.48rem 0.76rem;
  transition: background .5s;

  &:hover {
    background: #085096;
  }
`

export const MagicButton = styled.button`
  background: #05386B;
  border-style: solid;
  border-color: #5CDB95;
  border-width: 1px;
  font-family: Raleway;
  font-size: .76em;
  font-weight: 700;
  color: #EDF5E1;
  text-transform: uppercase;
  padding: 0.48rem 0.76rem;
  transition: background .5s;
  position: relative;

  &:hover{
    &::after {
      display: block;
      position: absolute;
      top: 0;
      left: 0;
      height: 100%;
      background: red;
      content: "";
    }
  }
`
