import styled from "styled-components";

export const LoginTitle = styled.h1`
  font-size: 2.8em;
  color: #EDF5E1;
  margin: 1.25rem 0 0;
  font-weight: inherit;
`

export const LoginSubtitle = styled.h2`
  font-size: 0.94em;
  margin: .2rem 0 1.25rem;
  color: #EDF5E1;
  font-weight: inherit;
`

export const LoginCGU = styled.h6`
  font-size: 0.6em;
  margin: .2rem 0 1.25rem;
  color: #EDF5E1;
  font-weight: inherit;
`

export const ErrorLogin = styled.div`
  font-size: 0.75em;
  margin: .2rem 0 1.25rem;
  color: #842029;
  font-weight: 700;
  width: 100%;
  background: #F8D7DA;
  padding: 0 2rem;
`
