import styled from "styled-components";

export const CalendarPageWrapper = styled.div`
  height: 100vh;
  width: 100vw;
  background: #05386B;
`
export const CalendarContainer = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
`

export const CalendarTimeColumn = styled.div`
  height: 100%;
  width: calc(100% / 8);
  float: right;
`
export const CalendarTimeText = styled.span`
  color: #5CDB95;
  font-size: 0.60em;
  height: calc(100% / 11);
  display: block;
  text-align: bottom;
`
