import styled, {createGlobalStyle} from "styled-components";

export const SetupWrapper = styled.div`
  height: 100vh;
  width: 100vw;
  display: grid;
  grid-template-columns: 8.5fr 5.5fr;
`

export const SetupFunctionalContainer = styled.div`
  background: #05386B;
  display: flex;
  align-items: center;
  padding: 0 4rem;
  box-sizing: border-box;
`

export const SetupHeroContainer = styled.div`
  background: #5CDB95;
  display: flex;
  align-items: center;
  justify-content: center;
`

export const SetupFunctionalContent = styled.div`

`

export const SetupStepIcon = styled.i`
  font-size: 1.73em;
  transition: color .5s;
  &:hover {
    color: #5CDB95;
  }
`

export const SetupHeroStyling = createGlobalStyle`
  img.setup-hero {
    width: 30rem;
  }
  i.setup-hero {
    font-size: 22rem;
  }

`
