import styled from "styled-components";

export const CalendarPageWrapper = styled.div`
  height: 100vh;
  width: 100vw;
  background: #05386B;
`

export const Calendar = styled.div`
  width: 57%;
  height: 55%;
  background: red;
  display: flex;
  justify-content: space-between;
`

export const CalendarColumn = styled.div`
  height: 100%;
  width: calc(100%/7 - .5rem);
  background: green;
`
