import styled from "styled-components";

export const CalendarPageWrapper = styled.div`
  height: 100vh;
  width: 100vw;
  background: #05386B;
  display: grid;
  padding: 0 4rem;
  box-sizing: border-box;
  grid-template-columns: ${props => props.inverted ? "8.5fr 5.5fr" : "8.5fr 5.5fr"};
  grid-template-areas: "header header" "calendar info";
  grid-column-gap: 3.61rem;
  grid-auto-rows: .90fr;

`
export const CalendarHeader = styled.div`
  grid-area: header;
  background: red;
  align-self: center;
`

export const CalendarWrapper = styled.div`
  display: flex;
  align-items: center;
  box-sizing: border-box;
  padding-right: 2.5rem;
  align-self: center;
  background: green;
`

export const CalendarContent = styled.div`
  width: 100%;
`

export const Calendar = styled.div`
  width: 100%;
  height: 55vh;
  display: flex;
  justify-content: space-between;
`

export const CalendarTimeColumn = styled.div`
  height: 100%;
  width: calc(100%/13 - .75rem);
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`

export const CalendarColumn = styled.div`
  height: 100%;
  width: calc(2*(100%/13) - .75rem);
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`

export const CalendarTime = styled.span`
  font-size: 0.60em;
  text-align: right;
  color: #5CDB95;
  transform: translateY(5px);
`
export const CalendarCell = styled.div`
  height: calc(100%/11);
  width: 100%;
  box-sizing: border-box;

`
export const CalendarDay = styled(CalendarCell)`
  font-size: 0.96em;
  display: flex;
  align-items: center;
  color: #5CDB95;
  border-bottom-color: #5CDB95;
  border-bottom-width: 2px;
  border-bottom-style: solid;
`
export const CalendarTimeSlot = styled(CalendarCell)`
  border-bottom-color: #5CDB95;
  border-bottom-width: 1px;
