import styled, {createGlobalStyle, css} from "styled-components";

export const SetupWrapper = styled.div`
  height: 100vh;
  width: 100vw;
  display: grid;
  grid-template-columns: 8.5fr 5.5fr;
`

export const SetupFunctionalContainer = styled.div`
  background: #05386B;
  display: flex;
  align-items: center;
  padding: 0 4rem;
  box-sizing: border-box;
`

export const SetupHeroContainer = styled.div`
  background: #5CDB95;
  display: flex;
  align-items: center;
  justify-content: center;
`

export const SetupFunctionalContent = styled.div`
  width: 100%;
`

export const SetupStepIcon = styled.i`
  font-size: 1.73em;
  transition: color .5s;
  &:hover {
    color: #5CDB95;
  }
`

export const SetupHeroStyling = createGlobalStyle`
  img.setup-hero {
    width: 30rem;
  }
  i.setup-hero {
    font-size: 21rem;
    color: #05386B;
  }
`
export const SetupH1 = styled.div`
  font-size: 2.8em;
  color: #EDF5E1;
  margin: .98rem 0 1.69rem;
`

export const SetupH2 = styled.div`
  font-size: 1.47em;
  margin: 0 0 1.13rem;
  color: #EDF5E1;
`

export const SetupH3 = styled.div`
  font-size: 0.94em;
  margin: ${props => props.last ? "0 0 1.13rem" : "0"};
  color: #EDF5E1;
`

export const SetupH4 = styled.div`
  font-size: 0.60em;
  margin: 0 0 1.09rem;
  color: #EDF5E1;
`

export const SetupFormWrapper = styled.div`
  width: 100%;
  max-height: 40vh;
  display: flex;
  justify-content: space-between;
`

export const SetupFormCategory = styled.div`
  width: calc(100%/3 - 1rem);
  display: flex;
  flex-direction: column;
  overflow: scroll;
`

export const SetupTextInputWrapper = styled.div`
  border-width: 1px;
  border-style: solid;
  border-color: #5CDB95;
  text-transform: uppercase;
  color: #5CDB95;
  text-align: center;
  font-size: .76em;
  box-sizing: border-box;
  padding: .5rem;
  margin-bottom: 0.56rem;
`

export const SetupInput = css`
 display: block;
 border: none;
 background: none;
 font: inherit;
 color: inherit;
 margin-top: .5rem;
 &:active, &:focus {
  outline : none;
 }
`

export const SetupDigitInput = styled.input`
 ${SetupInput};
 width: 2.5rem;
 text-align: center;
 margin: auto;
`

export const SetupTextInput = styled.textarea`
 ${SetupInput};
 width: 100%;
 resize: none;
 min-height: 4rem;
`
