import React, {forwardRef} from 'react';
import styled from "styled-components";

export const CalendarPageWrapper = styled.div`
  height: 100vh;
  width: 100vw;
  background: #05386B;
`
export const CalendarWrapper = styled.div`
  background: green;
`

export const Test = styled.div`
  height: ${props => (props.parentHeight / 8).toString().concat("px")};
  width: ${props => (props.parentWidth / 8).toString().concat("px")};
  background: red;
`

export class Calendar extends React.Component {
    renderChildren() {
        const {children} = this.props
        return React.Children.map(children, child => React.cloneElement(child, {
            parentHeight: this.props.absHeight,
            parentWidth: this.props.absWidth,
        }))
    }

    render() {
        let refFor = null
        React.forwardRef((props, ref) => {
            refFor = ref
        })
        return <div ref={refFor}>{this.props.children}</div>
    }
}
