import styled from "styled-components";

export const BasicButton = styled.button`
  background: #05386B;
  border-style: none;
  font-family: Raleway;
  font-size: .76em;
  font-weight: 700;
  color: #EDF5E1;
  text-transform: uppercase;
  padding: 0.48rem 0.76rem;
  transition: background .5s;

  &:hover {
    background: #085096;
  }
`
