import React, {Component} from 'react';
import styled from "styled-components";
import 'bootstrap-icons/font/bootstrap-icons.css'

const RequestRow = styled.tr`
  background-color: white;
`;

const RequestColumn = styled.td`
  text-align: center;
  padding: 2.25vh;
  border-width: 1px;
  border-color: #0A6466;
  font-family: Gilroy;
  font-weight: normal;
  color: #0A6466;
  font-size: 2.2vh;
  border-top-style: solid;
  border-bottom-style: solid;
  border-spacing: 0;
  &:first-child
  {
    border-style: solid;
    border-top-left-radius: 20px;
    border-bottom-left-radius: 20px;
    border-right: 0;
  }
  &:last-child
  {
    border-style: solid;
    border-top-right-radius: 20px;
    border-bottom-right-radius: 20px;
    border-left: 0;
  }
`;

const RequestStatus = styled.span`
  padding: 0.25vh;
  border-radius: 1px;
  width: 8vw;
  text-align: center;
`;

const RequestStatusDone = styled(RequestStatus)`
  color: #9CD0B7;
  background-color: #DEF1E8;
`;

const RequestUpdate = styled.button`
  display: inline-block;
  font-size: 1em;
  border-radius: 50%;
  height: 3.5vh;
  width: 3.5vh;
  border-width: 0;
`;

const RequestUpdateUndo = styled(RequestUpdate)`
  background-color:#FD453A;
  &:hover{
    background-color:#f1827d;
  }
`;

class Request extends Component {
    render() {
        return (
            <>
                <RequestRow>
                    <RequestColumn>2005222223INFO140</RequestColumn>
                    <RequestColumn>20 mai 2022</RequestColumn>
                    <RequestColumn>Salle 140</RequestColumn>
                    <RequestColumn>Matériel</RequestColumn>
                    <RequestColumn>Haut-parleurs</RequestColumn>
                    <RequestColumn><RequestStatusDone><i className="bi bi-check2"> </i>Validé</RequestStatusDone></RequestColumn>
                    <RequestColumn><RequestCancel><i className="bi bi-x"></i></RequestCancel></RequestColumn>
                </RequestRow><br/>
            </>
        );
    }
}

export default Request;
