import React from 'react';
import styled from "styled-components";
import 'bootstrap-icons/font/bootstrap-icons.css'

const Wrapper = styled.div`
    display: flex;
    background-color: #EFF8F8;
    height: 100%;
    width: 100%;
`;

const Navigation = styled.div`
    width: 22%;
    text-align: center;
`

const NavigationTitle = styled.h1`
    font-family: Gilroy;
    font-size : 1.5em;
    font-weight: bold;
    color: #0A6466;
`;
const NavigationActive = styled.button`
  font-family: Gilroy;
  font-weight: normal;
  font-size: 1em;
  padding: 0.6rem;
  width: 80%;
  border-width: 0;
  background-color: #0A6466;
  color: white;
  border-radius: 15px;
  margin-bottom: 0.8rem;
  &:hover {
    background-color: #228a8c;
  }
`;
const NavigationUnactive = styled.button`
  font-family: Gilroy;
  font-weight: normal;
  color: #0A6466;
  font-size: 1em;
  padding: 0.6rem;
  width: 80%;
  border-radius: 15px;
  margin-bottom: 0.8rem;
  background-color: #EFF8F8;
  border: 1px solid #0A6466;

  &:hover {
    background-color: #228a8c;
    color: white;
  }
`;
const Sprite = styled.i`
    font-size: 1em;
`;

const DashbordContent = styled.div`
    width: 78%;
`;
function Dashboard() {
        return (
            <Wrapper>
                <Navigation>
                    <NavigationTitle>l√©onTools</NavigationTitle>
                    <NavigationActive><Sprite className="bi bi-gear"> </Sprite>Tableau de bord</NavigationActive>
                    <NavigationUnactive><Sprite className="bi bi-file-earmark-bar-graph"> </Sprite>Statistiques</NavigationUnactive>
                </Navigation>

                <DashbordContent>

                </DashbordContent>
            </Wrapper>
        );
}

export default Dashboard;
