import React from 'react';
import styled from "styled-components";
import homeIcon from '../assets/icon_home.png'

function Login() {

    const Wrapper = styled.div`
      display: flex;
      height: 100%;
      width: 100%;
    `;

    const PictureContainer = styled.div`
      position: relative;
      display: flex;
      justify-content: center;
      align-items: center;
      background-color: #D2E5DC;
      height: 100%;
      width: 65%;
      border-bottom-right-radius: 12px;
      border-top-right-radius: 12px;
    `;

    const LoginPicture = styled.img`
      position: absolute;
      object-fit: cover;
      max-width: 85%;
      max-height: 100%;
      margin: 0;
    `;

    const LoginContainer = styled.div`
      display: flex;
      flex-direction: column;
      height: 100%;
      width: 35%;
      justify-content: center;
      align-items: center;
    `;

    const LoginTitle = styled.h1`
      font-family: Gilroy;
      font-weight: bold;
      font-size: 1.5em;
      color: #0A6466;
    `;

    const ErrorWrapper = styled.div`
    `;
    return (
        <Wrapper>
            <PictureContainer>
                <LoginPicture src={homeIcon}></LoginPicture>
            </PictureContainer>

            <LoginContainer>
                <LoginTitle>Connectez vous en utilisant Google</LoginTitle>
                <ErrorWrapper>Veuillez utiliser votre adresse professionnelle.</ErrorWrapper>
            </LoginContainer>
        </Wrapper>
    );
}

export default Login;
