import React from 'react';
import styled from "styled-components";
import Notification from "../components/Notification";
import 'bootstrap-icons/font/bootstrap-icons.css'
import Navigation from "../components/Navigation";

const Wrapper = styled.div`
    display: flex;
    background-color: #EFF8F8;
    height: 100%;
    width: 100%;
`;
const DashboardContent = styled.div`
    width: 78%;
    padding: 1rem;
`;

const WelcomeTitle = styled.h2`
    font-family: Gilroy;
    font-weight: normal;
    font-size: 1.1em;
    color: #0A6466;
    margin: 0;
    padding: 0;
`;

const WelcomeSubtitle = styled.h2`
    font-family: Gilroy;
    font-weight: bold;
    font-size: 1.1em;
    color: #0A6466;
    margin: 0;
    padding: 0;
`;

const CategoryTitle = styled.h3`
    font-family: Gilroy;
    font-weight: bold;
    font-size: 1em;
    margin-top: 1rem;
    margin-bottom: 1rem;
    color: #0A6466;
    padding: 0;
`;

const Notifications = styled.div`

    display: flex;
    justify-content: space-between;
    height: 30%;
    margin-bottom: 3rem;
`;

const Requests = styled.table`
    width:100%;
    border-collapse: separate;
    border-spacing:0;
`;

const RequestsHeader = styled.tr`
      font-family: Gilroy;
      font-weight: bold;
      font-size: 0.8em;
      color:#0A6466;
`;
function Dashboard() {
        return (
            <Wrapper>

                <Navigation/>

                <DashboardContent>

                    <WelcomeTitle>Bienvenue Mimoun</WelcomeTitle>
                    <WelcomeSubtitle>sur votre tableau de bord.</WelcomeSubtitle>

                    <CategoryTitle>Vos notifications</CategoryTitle>
                    <Notifications>
                        <Notification/>
                        <Notification/>
                        <Notification/>
                    </Notifications>

                    <CategoryTitle>Vos requêtes</CategoryTitle>
                    <Requests>
                        <RequestsHeader>
                            <td>Identifiant unique</td>
                            <td>Date de publication</td>
                            <td>Salle concernée</td>
                            <td>Type d’incident</td>
                            <td>Description du problème</td>
                            <td>Status</td>
                            <td>Mise à jour</td>
                        </RequestsHeader>
                    </Requests>

                </DashboardContent>
            </Wrapper>
        );
}

export default Dashboard;
