  background-color: #0A6466;
  color: white;
  border-radius: 15px;
  margin-bottom: 0.8rem;
  &:hover {
    background-color: #228a8c;
  }
`;

const NavigationLogout = styled.button`
  display: block;
  position: absolute;
  bottom: 0;
  font-family: Gilroy;
  font-weight: normal;
  font-size: 1em;
  padding: 0.6rem;
  width: 80%;
  border-width: 0;
  background-color: #842029;
  color: white;
  border-radius: 15px;
  margin-bottom: 0.8rem;
  transform: translateX(1.6rem);

  &:hover {
    background-color: #f57b85;
  }
`;

const NavigationInactive = styled.button`
  font-family: Gilroy;
  font-weight: normal;
  color: #0A6466;
  font-size: 1em;
  padding: 0.6rem;
  width: 80%;
  border-radius: 15px;
  margin-bottom: 0.8rem;
  background-color: #EFF8F8;
  border: 1px solid #0A6466;

  &:hover {
    background-color: #228a8c;
    color: white;
  }
`;
