    font-size: 1.1em;
    color: #0A6466;
    margin: 0;
    padding: 0;
`;

const WelcomeSubtitle = styled.h2`
    font-family: Gilroy;
    font-weight: bold;
    font-size: 1.1em;
    color: #0A6466;
    margin: 0;
    padding: 0;
`;

const CategoryTitle = styled.h3`
    font-family: Gilroy;
    font-weight: bold;
    font-size: 1em;
    margin-top: 1rem;
    margin-bottom: 1rem;
    color: #0A6466;
    padding: 0;
`;

const Notifications = styled.div`

    display: flex;
    justify-content: space-between;
