import React, {useEffect, useState} from 'react';
import styled from "styled-components";
import homeIcon from '../assets/icon_home.png'
import 'bootstrap-icons/font/bootstrap-icons.css'
import jwtDecode from "jwt-decode";

function Login() {
    function verifyUserValidity(response){
        const userObject = jwtDecode(response.credential);
        const userMail = userObject.email;
        const userDomain = userMail.split("@")[1]
        if (userDomain != ("leonafricain.ma" || "elbilia.ma")){
            setError("Veuillez utiliser votre adresse mail professionnelle")
            document.getElementById("error-wrapper").visibi
        }
    }

    useEffect(() => {
        /*global google*/
        google.accounts.id.initialize({
            client_id: "518591411336-6r7hes5r15e658cieinangeh7f39ctsb.apps.googleusercontent.com",
            callback: verifyUserValidity
        });
        google.accounts.id.renderButton(
            document.getElementById("signInDiv"),
            {theme:"outline", size:"large"}
        );
    },[]);

    const Wrapper = styled.div`
      display: flex;
      height: 100%;
      width: 100%;
    `;

    const PictureContainer = styled.div`
      position: relative;
      display: flex;
      justify-content: center;
      align-items: center;
      background-color: #D2E5DC;
      height: 100%;
      width: 65%;
      border-bottom-right-radius: 12px;
      border-top-right-radius: 12px;
    `;

    const LoginPicture = styled.img`
      position: absolute;
      object-fit: cover;
      max-width: 85%;
      max-height: 100%;
      margin: 0;
    `;

    const LoginContainer = styled.div`
      display: flex;
      flex-direction: column;
      height: 100%;
      width: 35%;
      justify-content: center;
      align-items: center;
    `;

    const LoginTitle = styled.h1`
      font-family: Gilroy;
      font-weight: bold;
      font-size: 1.5em;
      color: #0A6466;
    `;

    const ErrorWrapper = styled.div`
      display: none;
      font-family: Gilroy;
      color: #842029;
      background-color: #f8d7da;
      border: 1px solid #f5c2c7;
      padding: 0.5rem;
      border-radius: 5px;
      max-width: 85%;
    `;

    const [error, setError] = useState(null)

    return (
        <Wrapper>
            <PictureContainer>
                <LoginPicture src={homeIcon}></LoginPicture>
            </PictureContainer>

            <LoginContainer>
                <LoginTitle>Connectez vous en utilisant Google</LoginTitle>
                <ErrorWrapper id="error-wrapper">
                    <i className="bi bi-exclamation-triangle"> </i>
                    {error}
                </ErrorWrapper>
                <div id="signInDiv"></div>
            </LoginContainer>
        </Wrapper>
    );
}

export default Login;
