import React, {Component} from 'react';
import styled from "styled-components";

const Wrapper = styled.div`
    position: relative;
    width: 22%;
    text-align: center;
`

const NavigationTitle = styled.h1`
    font-family: Gilroy;
    font-size : 1.5em;
    font-weight: bold;
    color: #0A6466;
`;

const NavigationActive = styled.button`
  font-family: Gilroy;
  font-weight: normal;
  font-size: 1em;
  padding: 0.6rem;
  width: 80%;
  border-width: 0;
  background-color: #0A6466;
  color: white;
  border-radius: 15px;
  margin-bottom: 0.8rem;
  &:hover {
    background-color: #228a8c;
  }
`;

const NavigationLogout = styled.button`
  font-family: Gilroy;
  font-weight: normal;
  font-size: 1em;
  padding: 0.6rem;
  width: 80%;
  border-width: 0;
  background-color: #842029;
  color: white;
  border-radius: 15px;
  margin-bottom: 0.8rem;
  &:hover {
    background-color: #f57b85;
  }
`;

const NavigationInactive = styled.button`
  font-family: Gilroy;
  font-weight: normal;
  color: #0A6466;
  font-size: 1em;
  padding: 0.6rem;
  width: 80%;
  border-radius: 15px;
  margin-bottom: 0.8rem;
  background-color: #EFF8F8;
  border: 1px solid #0A6466;

  &:hover {
    background-color: #228a8c;
    color: white;
  }
`;
const Sprite = styled.i`
    font-size: 1em;
`;

class Navigation extends Component {
    render() {
        return (
            <Wrapper>
                <NavigationTitle>léonTools</NavigationTitle>
                <NavigationActive><Sprite className="bi bi-gear"> </Sprite>Tableau de bord</NavigationActive>
                <NavigationInactive><Sprite className="bi bi-file-earmark-bar-graph"> </Sprite>Statistiques</NavigationInactive>
                <NavigationLogout formaction="/"><Sprite className="bi bi-box-arrow-left"> </Sprite>Se déconnecter</NavigationLogout>
            </Wrapper>
        );
    }
}

export default Navigation;
