import React, {Component} from 'react';
import styled from "styled-components";

const Wrapper = styled.div`
  position: relative;
  width: 22%;
  text-align: center;
  @media screen and (max-width: 767px) {
    width: 100vw;
  }
`
const NavigationList = styled.div`
  width: 100%;
  @media screen and (max-width: 767px) {
    width: 100vw;
    display: none;
    transition-duration: 0.5s;
  }
`;

const NavigationTitle = styled.h1`
  font-family: Gilroy;
  font-size: 1.5em;
  font-weight: bold;
  color: #0A6466;
`;

const NavigationButton = styled.button`
  font-family: Gilroy;
  font-weight: normal;
  font-size: 1em;
  padding: 0.6rem;
  width: 80%;
  border-width: 0;
  border-radius: 15px;
  margin-bottom: 0.8rem;
`;

const NavigationActive = styled(NavigationButton)`
  background-color: #0A6466;
  color: white;

  &:hover {
    background-color: #228a8c;
  }
`;

const NavigationLogout = styled(NavigationButton)`
  background-color: #842029;
  color: white;

  &:hover {
    background-color: #f57b85;
  }
`;

const NavigationInactive = styled(NavigationButton)`
  color: #0A6466;
  background-color: #EFF8F8;
  border: 1px solid #0A6466;

  &:hover {
    background-color: #228a8c;
    color: white;
  }
`;
const Sprite = styled.i`
  font-size: 1em;
`;

const showNavigation = () => {

}
class Navigation extends Component {
    render() {
        return (
            <Wrapper>
                <NavigationTitle onClick={showNavigation}>léonTools</NavigationTitle>
                <NavigationList id="navigation-list">
                    <NavigationActive><Sprite className="bi bi-gear"> </Sprite>Tableau de bord</NavigationActive>
                    <NavigationInactive><Sprite
                        className="bi bi-file-earmark-bar-graph"> </Sprite>Statistiques</NavigationInactive>
                    <NavigationLogout><Sprite className="bi bi-box-arrow-left"> </Sprite>Se déconnecter</NavigationLogout>
                </NavigationList>
            </Wrapper>
        );
    }
}

export default Navigation;
