import React from 'react';
import {FontStyles} from "./assets/fonts/fonts";
import {BrowserRouter as Router, Route, Routes} from "react-router-dom";
import Login from "./pages/Login";
import TermsOfService from "./pages/TermsOfService";
import Dashboard from "./pages/Dashboard";
import getSessionFromDb from "./api/getSessionFromDb";
import getUserFromDb from "./api/getUserFromDb";

function App() {
        return (
            <>
                <FontStyles/>
                {function checkLogin(){
                    const sessionId = sessionStorage.getItem('sessionId')
                    if(sessionId){
                        getSessionFromDb(sessionId).then(response => {
                            getUserFromDb(response.data.google_id).then(userResponse => {
                                userResponse.data.verified ? console.log("dashboard") : console.log("cgu")
                            })
                        })
                    }

                }}
                <Router>
                    <Routes>
                        <Route path="/" element={<Login/>}></Route>
                        <Route path="/CGU" element={<TermsOfService/>}></Route>
                        <Route path="/dashboard" element={<Dashboard/>}></Route>
                    </Routes>
                </Router>
            </>
        );
}

export default App;
