import React from 'react';
import styled from "styled-components";
import Notification from "../components/Notification";
import 'bootstrap-icons/font/bootstrap-icons.css'

const Wrapper = styled.div`
    display: flex;
    background-color: #EFF8F8;
    height: 100%;
    width: 100%;
`;

const Navigation = styled.div`
    position: relative;
    width: 22%;
    text-align: center;
`

const NavigationTitle = styled.h1`
    font-family: Gilroy;
    font-size : 1.5em;
    font-weight: bold;
    color: #0A6466;
`;

const NavigationActive = styled.button`
  font-family: Gilroy;
  font-weight: normal;
  font-size: 1em;
  padding: 0.6rem;
  width: 80%;
  border-width: 0;
  background-color: #0A6466;
  color: white;
  border-radius: 15px;
  margin-bottom: 0.8rem;
  &:hover {
    background-color: #228a8c;
  }
`;

const NavigationLogout = styled.button`
  display: block;
  position: absolute;
  bottom: 0;
  font-family: Gilroy;
  font-weight: normal;
  font-size: 1em;
  padding: 0.6rem;
  width: 80%;
  border-width: 0;
  background-color: #842029;
  color: white;
  border-radius: 15px;
  margin-bottom: 0.8rem;
  transform: translateX(1.6rem);

  &:hover {
    background-color: #f57b85;
  }
`;

const NavigationInactive = styled.button`
  font-family: Gilroy;
  font-weight: normal;
  color: #0A6466;
  font-size: 1em;
  padding: 0.6rem;
  width: 80%;
  border-radius: 15px;
  margin-bottom: 0.8rem;
  background-color: #EFF8F8;
  border: 1px solid #0A6466;

  &:hover {
    background-color: #228a8c;
    color: white;
  }
`;
const Sprite = styled.i`
    font-size: 1em;
`;

const DashboardContent = styled.div`
    width: 78%;
    padding: 1rem;
`;

const WelcomeTitle = styled.h2`
    font-family: Gilroy;
    font-weight: normal;
    font-size: 1.1em;
    color: #0A6466;
    margin: 0;
    padding: 0;
`;

const WelcomeSubtitle = styled.h2`
    font-family: Gilroy;
    font-weight: bold;
    font-size: 1.1em;
    color: #0A6466;
    margin: 0;
    padding: 0;
`;

const CategoryTitle = styled.h3`
    font-family: Gilroy;
    font-weight: bold;
    font-size: 1em;
    margin-top: 1rem;
    margin-bottom: 1rem;
    color: #0A6466;
    padding: 0;
`;

const Notifications = styled.div`

    display: flex;
    justify-content: space-between;
    height: 30%;
`;
function Dashboard() {
        return (
            <Wrapper>
                <Navigation>
                    <NavigationTitle>léonTools</NavigationTitle>
                    <NavigationActive><Sprite className="bi bi-gear"> </Sprite>Tableau de bord</NavigationActive>
                    <NavigationInactive><Sprite className="bi bi-file-earmark-bar-graph"> </Sprite>Statistiques</NavigationInactive>
                    <NavigationLogout><Sprite className="bi bi-box-arrow-left"> </Sprite>Se déconnecter</NavigationLogout>
                </Navigation>

                <DashboardContent>
                    <WelcomeTitle>Bienvenue Mimoun</WelcomeTitle>
                    <WelcomeSubtitle>sur votre tableau de bord.</WelcomeSubtitle>

                    <CategoryTitle>Vos notifications</CategoryTitle>
                    <Notifications>
                        <Notification/>
                        <Notification/>
                        <Notification/>
                    </Notifications>

                    <CategoryTitle>Vos requêtes</CategoryTitle>

                </DashboardContent>
            </Wrapper>
        );
}

export default Dashboard;
