import React, {Component} from 'react';
import styled from "styled-components";
import 'bootstrap-icons/font/bootstrap-icons.css'

const Wrapper = styled.div`
    position: relative;
    padding: 0.8rem;
    background-color: #ffffff;
    border: 1px solid #0A6466;
    border-radius: 15px;
    width: 29.5%;
    height: 100%;
    overflow-x: scroll;
`;

const NotificationTitle = styled.h3`
      font-family: Gilroy;
      font-weight: bold;
      font-size: 1em;
      color: #0A6466;
      margin: 0;
      padding: 0;
`;

const NotificationAuthor = styled.h3`
      font-family: Gilroy;
      font-weight: normal;
      font-size: 1em;
      color: #0A6466;
      margin: 0;
      padding: 0;
`;

const NotificationText = styled.h3`
      font-family: Gilroy;
      font-weight: normal;
      font-size: 0.9em;
      color: #0A6466;
      margin-top: 0.5rem;
      padding: 0;
      text-align: justify;
      text-justify: inter-word;
`;

const NotificationMore = styled.button`
      position: absolute;
      bottom: 0;
      right: 0;
      margin: 0.5rem;
      color: #ffffff;
      font-size: 1.2em;
      background-color: #0A6466;
      border-width: 0;
      border-radius: 50%;
      height: 1.8rem;
      width: 1.8rem;

      &:hover {
        background-color: #228a8c;
      }
`;

class Notification extends Component {
    render() {
        return (
            <Wrapper>
                <NotificationTitle>Lorem Ipsum dolor Isemt</NotificationTitle>
                <NotificationAuthor>de Lorem Ipsum</NotificationAuthor>
                <NotificationText>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Proin eu leo ultricies, tincidunt libero in, consectetur lectus. Cras ante turpis, maximus ac luctus nec, rhoncus et est.</NotificationText>
                <NotificationMore><i className="bi bi-plus"></i></NotificationMore>
            </Wrapper>
        );
    }
}

export default Notification;
