import {leontoolsApi} from "./apiCall";
import axios from "axios";

function updateUser(googleId, role, verified) {
    return axios.put(leontoolsApi+"users/"+googleId, {
        google_id: googleId,
        role: "user",
        verified: verified
    })
}

export default updateUser;
